import React from 'react';

class MessageCenter extends React.Component{
	constructor(props){
		super(props);

		this.state ={
			selectedIndex: null
		}

		this.testData = [
			{
				correspondent: {
					name: 'Sarah Steel',
					profileImage: 'generic_person.jpg',
				},
				unReadMessages: 1,
				messageData: [
					{
						date: new Date().toDateString(),
						fromCorrespondent: true,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					},
					{
						date: new Date().toDateString(),
						fromCorrespondent: false,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					},
					{
						date: new Date().toDateString(),
						fromCorrespondent: true,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					}

				]



			},
			{
				correspondent: {
					name: 'John Doe',
					profileImage: 'morganFreeman.jpg',
				},
				unReadMessages: 6,
				messageData: [
					{
						date: new Date().toDateString(),
						fromCorrespondent: true,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					},
					{
						date: new Date().toDateString(),
						fromCorrespondent: false,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					},
					{
						date: new Date().toDateString(),
						fromCorrespondent: true,
						message: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat repudiandae unde, error illo hic, delectus? Expedita doloremque, reiciendis odit laboriosam voluptatem nam veritatis obcaecati ullam, eum pariatur aut, repellat hic!'

					}

				]



			}
		]
	}

	messageSelectorClick(i){
		this.setState({
			selectedIndex: i,
		})
	}


	render(){
		const messageSelectors = this.testData.map((data, index) =>
			<MessageSelector 
				name={data.correspondent.name} 
				profileImage={data.correspondent.profileImage}
				newMessages={data.unReadMessages}
				messageData={data.messageData}
				key={index}
				handleClick={(i) => this.messageSelectorClick(i)}
				selected={index===this.state.selectedIndex}
			/>
		)

		return(
			<div className='page-container message-center'>
				<div className='row'>
					<div className='col-lg-4 left' style={{borderRight: '1px solid'}}>
						<div className='row'>
							<div className='col-lg-10'>
								<input placeholder='Find classmates' type="text"/>
							</div>
							<div className='col-lg-2'>
								<button>+</button>
							</div>
						</div>
						<div className='message-selector-container'>
							{messageSelectors}
						</div>

					</div>
					<div className='col-lg-8'>
						<div className='right'>
							<MessageContainer
								messageData={this.testData[0].messageData}
								profileImage={this.testData[0].correspondent.profileImage}
								name={this.testData[0].correspondent.name}
								noneSelected={!this.state.selectedIndex}
							/>
							<div className='reply'>
									<textarea></textarea>
							</div>
						</div>
					</div>
				</div>
			</div>

		)
	}
}

function MessageContainer(props){

	const messages = props.messageData.map((data, index) =>
		<Message 
			key={index}
			date={data.date}
			fromCorrespondent={data.fromCorrespondent}
			message={data.message}
		/>
	)

	alert(props.noneSelected)

	return(
		<div className='messages-container'>
			<div className='header'>
				<img src={props.profileImage} alt="Can't display photo"/>
				<h1>{props.name}</h1>
			</div>
			<div className='messages-body'>
				{messages}
			</div>
		</div>
	);
}

function Message(props){
	const receivedOrSent = props.fromCorrespondent ? 'received' : 'sent';
	return (
		<div className='messages'>
			<h5>{props.date}</h5>

			<div className={receivedOrSent}>
				<p>{props.message}</p>
			</div>
		</div>
	)
}


function MessageSelector(props){
	const newMessages = props.newMessages > 0 ? <h2>{props.newMessages} New Message</h2>: null;
	const lastMessage = props.messageData[props.messageData.length-1].message;

	const selected = props.selected ? selectedMessageStyle: null;

	return(
		<div className='message-selector' style={selected}>
			<div className='row'>
				<div className='col-lg-1'>
					<img src={props.profileImage} alt="Can't display photo"/>
				</div>
				<div className='col-lg-7'>
					<h1>{props.name}</h1>
				</div>
				<div className='col-lg-4'>
					{newMessages}
				</div>
			</div>
			<h3>5 Days Ago</h3>
			<p>{lastMessage}</p>
		</div>
	);
}

const selectedMessageStyle = {
	background: 'red',
}

export default MessageCenter;
