import React from 'react';
import StartEndTime from './StartEndTimeComp/StartEndTime';
import {FlipInOut, BackInOut_HandleState, FadeInOut_HandleState} from './CustomTransition';
import {TimeHR}  from './StartEndTimeComp/StartEndTime';
import {convertToMilitary} from './Agenda_Helper';
import {findProportionalTimeDif, convertToStandardNum, increment, findIncrementVal, decrement, roundTimeDown} from './StartEndTimeComp/helperFunc';

class SemesterCreator extends React.Component{
	constructor(props){
		super(props);

		this.state ={
			semData: {
				name: null,
				classData: [],
			},
			currentClass: {
				name: null,
				instructor: null,
				location: null,
				daysOfWeek: [],
				time: {
					start: null,
					end: null,
				},
				date: {
					start: null,
					end: null,
				}
			}
		}
	}

	semName(key, text){
		if(key==="Enter"){
			const semData_copy = this.state.semData;
			semData_copy.name= text;
			this.setState({
				semData: semData_copy,
			});
		}
	}

	updateCurrent(key, text){
		const currentClass_copy = this.state.currentClass;
		currentClass_copy[key] = text;

		this.setState({
			currentClass: currentClass_copy,
		});
	}

	render(){
		return(
			<div className='semester-creator-container'>
					<div>
						<h1>{this.state.semData.name} text</h1>
						<hr/>

						<ClassEditor updateCurrent={(key, text) => this.updateCurrent(key, text)}/>

						<SuggestedLinksContainer currentClass={this.state.currentClass}/>
					</div>
			</div>
		);
	}
}

