    for (let j = 0; j < sortedArr.length; j++) {
      const relTimeVal = convertToMilitary(sortedArr[j].time.start);

      if (timeVal < relTimeVal) {
        const temp = sortedArr[i];
        sortedArr[i] = sortedArr[j];
        sortedArr[j] = temp;
      }
    }
  }

  return sortedArr;
}

