		const firstEvent = sortedData[0].time.start;
		const lastEvent = sortedData[sortedData.length-1].time.start;
		const timeDif = findProportionalTimeDif(firstEvent, lastEvent );
		const intervals = Math.floor(timeDif/intervalDivider);
		const firstEvent_stdNum = convertToStandardNum(convertToMilitary(firstEvent));

		let arr = []

		for(let i =0; i < intervals; i++){
			const currentTimeInt = [intervalDivider * i + firstEvent, intervalDivider * (i+1) + firstEvent];


			// arr[i] = findRowData(sortedData, currentTimeInt);


			let weekArr = []
				for(let j =0; j< 7; j++){
					let dayArr = [];

					sortedData.forEach(function(o){
						let ct =0;
						for(let day in o.weekDays){
							if(ct === j && o.weekDays[day]){
								dayArr.push('yes')
							}else{
								dayArr.push(null);
							}

							ct++;
						}
						console.log(dayArr)
					})



					weekArr.push(dayArr);
				}


				// sortedData.forEach(function(o){
				// 	let dayArr =[];
				// 	for(let day in o.weekDays){
				// 		if(currentTimeInt[0] <= o.time.start && o.time.start <= currentTimeInt[1]){
				// 			dayArr.push('yes')
				// 		}else{
				// 			dayArr.push(null);
				// 		}
				// 	}
				// 	weekArr.push(dayArr);
				// });

			arr.push(weekArr);
		}

		console.log(arr);

		return(
			<div className='seven-day-agenda'>
				<table>
					<thead>
						<tr>
							<th>Mon.</th>
							<th>Tue.</th>
							<th>Wed.</th>
							<th>Thu.</th>
							<th>Fri.</th>
							<th>Sat.</th>
							<th>Sun.</th>
						</tr>
					</thead>
				</table>
			</div>
		);
	}
}

function sortByStartTime(arr){
	let sortedArr = arr;

	for(let i =0; i <sortedArr.length; i++){
		const timeVal = convertToMilitary(sortedArr[i].time.start);

		for(let j =0; j< sortedArr.length; j++){
			const relTimeVal = convertToMilitary(sortedArr[j].time.start);

			if(timeVal < relTimeVal){
				const temp = sortedArr[i];
				sortedArr[i] = sortedArr[j];
				sortedArr[j] = temp;
			}
		}
	}
	return sortedArr;

}

