import React from 'react';
import {convertToMilitary, convertToStandard} from './Agenda_Helper';

class SemesterCreator extends React.Component{
	render(){
		return(
			<div className='semester-creator-container'>
				<div className='row'>
					<div className='col-lg-6'>
						<div className='class-item-container'>
							<ClassItem/>
							<ClassItem/>
							<ClassItem/>
							<ClassItem/>	
						</div>

						<input type="text" placeholder='Class Name'/>
						<button>M</button>
						<button>T</button>
						<button>W</button>
						<button>T</button>
						<button>F</button>
						<button>S</button>
						<button>S</button>
						<div className='row'>
							<div className='col-lg-6'>
								<StartEndTime/>
							</div>
							<div className='col-lg-6'>
								<input type="text" placeholder='Instructor'/>
								<input placeholder='location'/>
							</div>
						</div>
					</div>
					<div className='col-lg-6'>
						<div className='seven-day-agenda'>
							<table>
								<thead>
									<tr>
										<th>Mon.</th>
										<th>Tue.</th>
										<th>Wed.</th>
										<th>Thu.</th>
										<th>Fri.</th>
										<th>Sat.</th>
										<th>Sun.</th>
									</tr>
								</thead>
							</table>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

class StartEndTime extends React.Component{
	constructor(props){
		super(props);

		this.state={
			startTime: '2:00PM',
			endTime: '4:00PM',
		}

		this.topInterval = null;
	}

	incrementTop(){
		let ct =1;
		this.topIntreval = setInterval(
			() => {
				const timeCopy = this.state.startTime
				this.setState({
					startTime: increment(ct++, timeCopy)
				})

			} 

		, 500)
	}
	stopIncrementTop(){
		clearInterval(this.topIntreval);
	}

	render(){
		return(
			<div className='start-end-time-container'>
				<div className='time-container'>
						<hr/>
						<hr/>
						<hr/>
						<hr/>
						<hr/>
						<hr/>
						<div className='top-con'>
							<button onMouseUp={() => this.stopIncrementTop()} onMouseDown={() => this.incrementTop()} className='up'><i class="fas fa-caret-up"></i></button>
							<button className='down'><i class="fas fa-caret-down"></i></button>
						</div>
						<div className='timeDis'>
							<input value={this.state.startTime} className='topIn' type="text" placeholder='12:00AM'/>
							<input value={this.state.endTime} className='bottomIn' type="text" placeholder='2:00PM'/>
						</div>
						<div className='bottom-con'>
							<button className='up'><i class="fas fa-caret-up"></i></button>
							<button className='down'><i class="fas fa-caret-down"></i></button>
						</div>
				</div>

			</div>
		);
	}
}

function increment(ct, time){
	const timeMT = convertToMilitary(time);
	let incrementVal = findIncrementVal(ct);
	let incrmentedMT;

	if(ct < 6){
		let previousIncVal = findIncrementVal(ct-1);
		incrmentedMT = timeMT + incrementVal-previousIncVal;
	}else{
		incrmentedMT = timeMT + incrementVal;
	}

	const returnValStr = incrmentedMT + '';

	if(returnValStr.length>1){
		if(returnValStr.substring(returnValStr.length-2, returnValStr.length) === '60'){
			incrmentedMT= incrmentedMT+40
		}
	}


}

function findIncrementVal(ct){
	switch(ct){
		case 1: return 5;break;
		case 2: return 10;break;
		case 3: return 15;break;
		case 4: return 30;break;
		case 5: return 100;break;
	}

	if(ct >=6){
		return 200;
	}else{
		return 0;
	}
}

function ClassItem(props){
	return(
		<div className='class-item'>
			<div className='row'>
				<div className='col-lg-3'>
					<h1>Algebra</h1>
				</div>
				<div className='col-lg-3'>
					<h2>Dr. Lee</h2>
				</div>
				<div className='col-lg-4'>
					<h3>M T W Th F S S</h3>
				</div>
				<div className='col-lg-2'>

				</div>
			</div>
			<button><i class="fas fa-trash"></i></button>
		</div>
	)
}

export default SemesterCreator;
