function toSingleCharArr(twoDArr){
	if(twoDArr){
		let returnArr =[];

		for(let i =0; i< twoDArr.length; i++){
			const tempArr = twoDArr[i];
			for(let j =0; j<tempArr.length; j++){
				returnArr.push(tempArr[j]);
			}
		}

		return returnArr;
	}else{
		return null;
	}
}

