	console.log(testLength, testVal, dataString)
	if(testLength && testLength >0){
		let ct =0;
		for(let i =0; i<testLength; i++){
			if(testVal[i] == dataString[i] && testVal[i] !== '0'){
				ct++;
			}
		}
		console.log(ct/testLength)
		return ct/testLength;
	}else{
		return 1;
	}
}

function Soundex(txt){
	if(txt && txt.length){
		const words = txt.trim().split(' ');
		let returnArr = [];

		words.forEach(function(o){
			let txtArr = o.toLowerCase().split('');

			for(let i =1; i< txtArr.length; i++){
				if(txtArr[i] === 'b' || txtArr[i] === 'f' || txtArr[i] === 'p' || txtArr[i] === 'v'){
					txtArr[i] = '1';
				}else if(txtArr[i] === 'c' || txtArr[i] === 'g' || txtArr[i] === 'j' || txtArr[i] === 'k' || txtArr[i] === 'q' || txtArr[i] === 's' || txtArr[i] === 'x' || txtArr[i] === 'z'){
					txtArr[i] = '2';
				}else if(txtArr[i] === 'd' || txtArr[i] === 't'){
					txtArr[i] = '3';
				}else if(txtArr[i] === 'l'){
					txtArr[i] = '4';
				}else if(txtArr[i] === 'm' || txtArr[i] === 'n'){
					txtArr[i] = '5';
				}else if(txtArr[i] === 'r'){
					txtArr[i] = '6';
				}

				if(i+1 < txtArr.length){
					if(txtArr[i] === txtArr[i+1]){
						txtArr.splice(i+1, i+2);
					}
				}

				if(i+2 < txtArr.length){
					if(txtArr[i] === txtArr[i+2]){
						txtArr.splice(i+2, i+3);
					}
				}

				if(txtArr[i] === 'a' || txtArr[i] === 'e' || txtArr[i] === 'i' || txtArr[i] === 'o' || txtArr[i] === 'u' || txtArr[i] === 'y' || txtArr[i] === 'h' || txtArr[i] === 'w' ){
					txtArr.splice(i, i+1);
				}
			}

			while(txtArr.length<4){
				txtArr.push('0');
			}

			if(txtArr.length>4){
				txtArr.splice(4, txtArr.length);
			}

			returnArr.push(txtArr);
		});
		return returnArr;
	}
	return null;
}

class MessageContainer extends React.Component{

	scrollToBottom = () => {
	  this.messagesEnd.scrollIntoView();
	}

	componentDidMount() {
	  this.scrollToBottom();
	}

	componentDidUpdate() {
	  this.scrollToBottom();
	}

	render(){

		const messages = this.props.messageData.map((data, index) =>
			<Message 
				key={index}
				date={data.date}
				fromCorrespondent={data.fromCorrespondent}
				message={data.message}
			/>
		)

		return(
			<div className='messages-container'>
				<div className='header'>
					<img src={this.props.profileImage} alt="Can't display photo"/>
					<h1>{this.props.name}</h1>
				</div>
				<div className='messages-body'>
					{messages}
					<div style={{ float:"left", clear: "both" }}
			            ref={(el) => { this.messagesEnd = el; }}>
			        </div>
				</div>
			</div>
		);
	}
}

function Message(props){
	const receivedOrSent = props.fromCorrespondent ? 'received' : 'sent';
	return (
		<div className='messages'>
			<h5>{props.date}</h5>
			<div className={receivedOrSent}>
				<p>{props.message}</p>
			</div>
		</div>
	)
}


function MessageSelector(props){
	const newMessages = props.newMessages > 0 ? <h2>{props.newMessages} New Message</h2>: null;
	const lastMessage = props.messageData[props.messageData.length-1].message;

	const selected = props.selected ? 'selected': null;

	return(
		<div className={'message-selector ' + selected} onClick={() => props.handleClick(props.i)}>
			<div className='row'>
				<div className='col-lg-1'>
					<img src={props.profileImage} alt="Can't display photo"/>
				</div>
				<div className='col-lg-7'>
					<h1>{props.name}</h1>
				</div>
				<div className='col-lg-4'>
					{newMessages}
				</div>
			</div>
			<h3>5 Days Ago</h3>
			<p>{lastMessage}</p>
		</div>
	);
}

export default MessageCenter;
