class Link extends React.Component{
	constructor(props){
		super(props);

		this.state={
			isExpanded: false,
		}
	}

	toggleExpanded(){
		const isExpandedCopy = this.state.isExpanded;

		this.setState({
			isExpanded: ! isExpandedCopy,
		})
	}

	render(){
		const display = this.state.isExpanded ? <ExpandedLink name={props.name} links={props.links} user={props.user} location={props.location} instructor={props.instructor} weekDays={props.weekDays} toggleExpanded={() => this.toggleExpanded()}/> : <ShortLink data={this.props.data} toggleExpanded={() => this.toggleExpanded()}/>
		return(
			{display}
		);
	}
}

