import React from 'react';

class PostCreator extends React.Component{
	constructor(props){
		super(props);

		this.fileInput = React.createRef();
	}

	addImages(){
		alert('asdf');
	}

	render(){
		return(
			<div className="make-post mont-font">
	    		<h5><span style={{fontWeight: 600}}>{this.props.firstName}</span>, what happened today in class?</h5>
		        <p><span className='textarea' role='textbox' contenteditable='true'></span></p>
				<button 
					onClick={() => this.fileInput.current.click()} 
					style={{background: 'none'}} 
					className='white-c'>
					<i class="fas fa-camera-retro"></i>
				</button>
				<button className='submit blue-bc'>Submit</button>
				<input 
					type="file" 
					ref={this.fileInput} 
					style={{display: 'none'}}
					accept="image/png, image/jpeg"
					multiple={true}
					onChange={() => this.addImages(this.fileInput.current.files)}
				/>
	      	</div>
		);
	}
}

export default PostCreator;
