				errors_copy[key] = true;
				this.error = true;
			}else{
				errors_copy[key] = false;
			}
		}

		return errors_copy;
	}

	checkDaysOfWeek(daysOfWeek_error_copy, currentClass){
		let ct =0;
		currentClass.daysOfWeek.forEach((x) =>{
			if(x){
				ct++;
			}
		})

		if(ct<1){
			daysOfWeek_error_copy = true;
			this.error= true;
		}else{
			daysOfWeek_error_copy = false;
		}
		return daysOfWeek_error_copy;
	}

	checkStartEndDate(){
				if(!errors_copy.startDate && !errors_copy.endDate){
			const startArr = this.props.currentClass.date.start.split('/');
			const endArr = this.props.currentClass.date.end.split('/');
			if(checkDate(startArr)){
