import React from 'react';
import {FadeInOut} from './CustomTransition';

class ClassView extends React.Component{
	constructor(props){
		super(props);

		this.state={
			testData: {
				name: 'Freshman 1st Semester',
				classes: [
					{
						name: 'Algebra',
						instructor: 'Dr. Lee',
						location: 'Zurn 101',
						startTime: '10:15AM',
						endTime: '11:05AM',
						assignments: [
							{
								name: 'Hw1',
								dueDate: 'Tomorrow',
								description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sint cum iure, velit soluta. Sapiente sint unde quasi sequi reiciendis, eaque molestias, similique saepe doloribus consequatur, ratione doloremque odio rerum tempora!',
								completed: false,
							},
							{
								name: 'Hw1',
								dueDate: 'Tomorrow',
								description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sint cum iure, velit soluta. Sapiente sint unde quasi sequi reiciendis, eaque molestias, similique saepe doloribus consequatur, ratione doloremque odio rerum tempora!',
								completed: true,
							}
						]
					},
					{
						name: 'Algebra',
						instructor: 'Dr. Lee',
						location: 'Zurn 101',
						startTime: '10:15AM',
						endTime: '11:05AM',
						assignments: [
							{
								name: 'Hw1',
								dueDate: 'Tomorrow',
								description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sint cum iure, velit soluta. Sapiente sint unde quasi sequi reiciendis, eaque molestias, similique saepe doloribus consequatur, ratione doloremque odio rerum tempora!',
								completed: false,
							},
							{
								name: 'Hw1',
								dueDate: 'Tomorrow',
								description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sint cum iure, velit soluta. Sapiente sint unde quasi sequi reiciendis, eaque molestias, similique saepe doloribus consequatur, ratione doloremque odio rerum tempora!',
								completed: true,
							}
						]
					}
				]
			}
		}
	}

	render(){
		const classes = this.state.testData.classes.map((data, index) =>
			<ClassCon  
				name={data.name}
				instructor={data.instructor}
				location={data.location}
				startTime={data.startTime}
				endTime={data.endTime}
				assignments={data.assignments}
				key={index}
			/>
		)

		return(
			<div className='class-view-container'>
				<div className='semester-container'>
					<h1>{this.state.testData.name}</h1>
					<button>...</button>
				</div>
				<h5>{this.state.testData.classes.length} Classes</h5>
				<hr/>
				{classes}
			</div>
		);
	}
}

class ClassCon extends React.Component{
	constructor(props){
		super(props);

		this.state={
			showAssignment: false,
		}
	}

	render(){
		return(
			<div className='class-container'>
				<button className='link' >Link</button>
				<h1>{props.name}</h1>
				<h2>{props.instructor}</h2>
				<h3>{props.location}</h3>
				<h4>{props.startTime} - {props.endTime}</h4>
				<button className='see-assign'><i class="fas fa-chevron-down"></i> See Assignments</button>
				<AssignContainer assignments={props.assignments}/>
			</div>
		);
	}
}

function AssignContainer(props){
	const upComAssign = props.assignments.map((data, index) =>
		data.completed ? null : 
			<Assign
				name={data.name}
				dueDate={data.dueDate}
				description={data.description}
			/>
	);

	const compAssign = props.assignments.map((data, index) =>
		data.completed ? 
			<Assign
				name={data.name}
				dueDate={data.dueDate}
				description={data.description}
				key={index}
			/>
		: null
	);

	return(
		<div className='assign-container'>
			<h5>Upcomming</h5>
			<hr/>
			{upComAssign}
			<h5 style={{marginTop: 10}}>Completed</h5>
			<hr/>
			{compAssign}
		</div>
	);
}

function Assign(props){
	return(
		<div className='assign'>
			<h1>{props.name}</h1>
			<h2>{props.dueDate}</h2>
			<button>...</button>
			<p>{props.description}</p>
		</div>
	);
}

export default ClassView;
