class CE_Errors{
	constructor(){
		this.error = false;
	}

	checkInputs(errors_copy, currentClass){
		for(let key in errors_copy){
			if(currentClass[key] || currentClass[key].length < 1){
				errors_copy[key] = true;
				this.error = true;
			}else{
				errors_copy[key] = false;
			}
		}

		return errors_copy;
	}

}
export default CE_Errors;
