import React from 'react';

class SemesterCreator extends React.Component{
	render(){
		return(
			<div className='semester-creator-container'>
				<div className='row'>
					<div className='col-lg-6'>
						<div className='class-item-container'>
							<ClassItem/>
							<ClassItem/>
							<ClassItem/>
							<ClassItem/>	
						</div>

						<input type="text" placeholder='Class Name'/>
						<button>M</button>
						<button>T</button>
						<button>W</button>
						<button>T</button>
						<button>F</button>
						<button>S</button>
						<button>S</button>
						<div className='row'>
							<div className='col-lg-6'>
								<StartEndTime/>
							</div>
							<div className='col-lg-6'>
								<input type="text" placeholder='Instructor'/>
								<input placeholder='location'/>
							</div>
						</div>
					</div>
					<div className='col-lg-6'>
						<div className='seven-day-agenda'>
							<table>
								<thead>
									<tr>
										<th>Mon.</th>
										<th>Tue.</th>
										<th>Wed.</th>
										<th>Thu.</th>
										<th>Fri.</th>
										<th>Sat.</th>
										<th>Sun.</th>
									</tr>
								</thead>
							</table>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

class StartEndTime extends React.Component{
	constructor(props){
		super(props);

		this.dragBottom = React.createRef();

		this.state={
			bottomMouseDown: false,
			bottomPrevPosition_X: null,
  			bottomPrevPosition_Y: null,
  			bottomCurPosition_X: null,
  			bottomCurPosition_Y: null,
		}
	}

	bottomMouseDown(event){
		console.log(event.clientX)
		this.setState({
			bottomMouseDown: true,
			bottomPrevPosition_X: event.clientX,
  			bottomPrevPosition_Y: event.clientY,
		})
	}

	bottomMouseMove(event){
	  	if(this.state.bottomMouseDown){
		  	let previousPosition_X = this.state.bottomPrevPosition_X;
		  	let previousPosition_Y = this.state.bottomPrevPosition_Y;
		  	let currentPos_X = this.state.bottomCurPosition_X;
		  	let currentPos_Y = this.state.bottomCurPosition_Y;

		  	let newPos_X = currentPos_X-(previousPosition_X-event.clientX);
		  	let newPos_Y = currentPos_Y-(previousPosition_Y-event.clientY);
	  		this.setState({
	  			currentPosition_X: newPos_X,
	  			currentPosition_Y: newPos_Y,
	  			previousPosition_X: event.clientX,
	  			previousPosition_Y: event.clientY,
	  		})
	  	}
  	}

  	bottomMouseUp(){
  		this.setState({
  			bottomMouseDown: false,
  		})
  	}

	render(){
		return(
			<div className='start-end-time-container'>
				<div className='time-container'>
					<div>
						<button>DragUp</button>
						<button 
							style={{top: this.state.bottomCurPosition_Y,left: this.state.bottomCurPosition_X, position: 'absolute'}} 
							onMouseMove={(e) => this.bottomMouseMove(e)} 
							onMouseDown={(e) => this.bottomMouseDown(e)}
							onMouseUp={() => this.bottomMouseUp()}
							ref={this.dragBottom}
						>DragDown</button>
					</div>
				</div>
				<input type="text" placeholder='12:00AM'/>
				<input type="text" placeholder='2:00PM'/>
			</div>
		);
	}
}

function ClassItem(props){
	return(
		<div className='class-item'>
			<div className='row'>
				<div className='col-lg-3'>
					<h1>Algebra</h1>
				</div>
				<div className='col-lg-3'>
					<h2>Dr. Lee</h2>
				</div>
				<div className='col-lg-4'>
					<h3>M T W Th F S S</h3>
				</div>
				<div className='col-lg-2'>

				</div>
			</div>
			<button><i class="fas fa-trash"></i></button>
		</div>
	)
}

export default SemesterCreator;
