import React from 'react';
import {FadeInOut_HandleState} from './CustomTransition';

class PostCreator extends React.Component{
	constructor(props){
		super(props);
		this.state={
			images: null,
		}

		this.fileInput = React.createRef();

	}

	addImages(files){
		let tempArr = [];

		let reader = new FileReader();
		reader.onload = (e) => {
			tempArr.push(e.target.result);
			this.setState({
				images: tempArr,
			})
		}

		files.forEach((file) => {
			reader.readAsDataUrl(file);
		});
	}

	render(){
		if(this.state.images){
			const images = this.state.images.map((data, index) =>
				<div className='col-lg-3'>
					<img src="data" alt=""/>
				</div>
			);
		}
		return(
			<div className="make-post mont-font">
	    		<h5><span style={{fontWeight: 600}}>{this.props.firstName}</span>, what happened today in class?</h5>
		        <p><span className='textarea' role='textbox' contenteditable='true'></span></p>
		        <FadeInOut_HandleState condition={this.state.images !== null}>
			        <div className='row'>
			        	{images}
			        </div>
			    </FadeInOut_HandleState>
				<button 
					onClick={() => this.fileInput.current.click()} 
					style={{background: 'none'}} 
					className='white-c'>
					<i class="fas fa-camera-retro"></i>
				</button>
				<button className='submit blue-bc'>Submit</button>
				<input 
					type="file" 
					ref={this.fileInput} 
					style={{display: 'none'}}
					accept="image/png, image/jpeg"
					multiple={true}
					onChange={() => this.addImages(this.fileInput.current.files)}
				/>
	      	</div>
		);
	}
}

export default PostCreator;
