import React from 'react';
import AssignmentDashboard from './AssignmentDashboard';
import {NewAssignment} from './AssignmentDashboard';


class App extends React.Component{
	constructor(props){
		super(props);

		this.state = {
			showNewAssignmentForm: false,
			classes: ['Algebra', 'Geometry', 'Geology', 'Philosophy', 'Chemistry', 'Biology', 'English', 'Political Science']
		}
	}

	handleNewAssignment(){
		this.setState({
			showNewAssignmentForm: true,
		})
	}

	render(){
		let newAssignment;
		if(this.state.showNewAssignmentForm){
			newAssignment = <NewAssignment />
		}

		return(
		    <div class="page-container">
		      <div class="row">
		        <div class="col-lg-2 menu">
		          <ul>
		            <li>List Item 1</li>
		            <li>List Item 1</li>
		            <li>List Item 1</li>
		            <li>List Item 1</li>
		            <li>List Item 1</li>
		            <li>List Item 1</li>
		          </ul>
		        </div>
		        <div class="col-lg-6 feed-container">
		          <div class="make-post">
		            <textarea></textarea>
		            <button>Submit</button>
		          </div>
		          <div class="feed-data">

		          </div>
		        </div>
		        <div class="col-lg-4 dash">
		          <div class="assignments-container">
		            <AssignmentDashboard handleNewAssignment={this.handleNewAssignment()}/>
		          </div>
		          <div class="row agenda">
		            <h1>Agenda</h1>  
		          </div>

		        </div>
		      </div>
		      {newAssignment}
		    </div>
  		);
	}

}

export default App;
